let lastcoin = 0;

exports.throttle = function throttle(func, delay) {
	let now = Date.parse(new Date());

	if (now - lastcoin >= delay) {
		func();
		lastcoin = Date.parse(new Date());
	}
};

exports.countdown = function countdown(endtime) {
	var t = Date.parse(endtime) - Date.parse(new Date());
	var seconds = Math.floor((t / 1000) % 60);
	var minutes = Math.floor((t / 1000 / 60) % 60);
	var hours = Math.floor((t / (1000 * 60 * 60)) % 24);
	var days = Math.floor(t / (1000 * 60 * 60 * 24));
	var total_time = `${days} days, ${hours} hours, ${minutes} minutes, ${seconds} seconds`;
	return {
		milliseconds: t,
		days: days,
		hours: hours,
		minutes: minutes,
		seconds: seconds,
		totaltime: total_time
	};
};
